/// <reference path="../references.ts" />
/// <reference path="../interfaces.ts" />
/// <reference path="IModuleExport.ts" />
/// <reference path="Attribute.ts" />
/// <reference path="Reflection.ts" />


module appex.modules {
    
    /** interface for modules */
    export interface IModule extends appex.IDisposable {
    
        exports     : appex.modules.IModuleExport[];

        /** the vm context. (exported objects accessible on context.exports) */
        context     : any;

        /** the javascript this module is mapping */
        javascript  : string;

        /** type reflection meta data for this module */
        reflection  : appex.modules.Reflection;

        /** compiler diagnostics for this module. */
        diagnostics : typescript.api.Diagnostic[];

        /** attributes for this module. */
        attributes  : appex.modules.AttributeCache;
    }
}