/// <reference path="../modules/IModule.ts" />
/// <reference path="JsonSchemaGenerator.ts" />
/// <reference path="JsonSchemaValidator.ts" />
/// <reference path="JsonSchemaValidatorError.ts" />


module appex.schema {

    /** appex - json schema api.
     *  acts as a container for the generator and validator.
     */
	export class JsonSchema {

        private reflection : appex.modules.Reflection;

		constructor(public module: appex.modules.IModule) { 
        
        }

        /** generate a json schema object 
        *   from a class or interface definition. if the definition cannot be located, will return null.
        *   
        *   arguments:
        *       qualifier - the fully qualified name of the type being generated.
        *       returns   - a json schema, or null if type not found.
        */
        public generate (qualifier:string) : appex.schema.JsonSchemaNode {
        
            var generator = new appex.schema.JsonSchemaGenerator(this.module.reflection);

            return generator.get(qualifier);
        }

        /** validates a json object against a class or interface definition.
        *   
        *   arguments:
        *       qualifier - the fully qualified name of the type being validated against.
        *       returns   - a array of json schema validation errors, or a empty array if none.
        */
        public validate(qualifier:string, object:any) : appex.schema.JsonSchemaValidatorError[] {
            
            if(object == null) {
                
                return [new appex.schema.JsonSchemaValidatorError('unable to validate null object.')]

            }

            var generator = new appex.schema.JsonSchemaGenerator(this.module.reflection);

            var schema = generator.get(qualifier);

            if(schema) {
            
                var validator = new appex.schema.JsonSchemaValidator();
                
                return validator.validate(object, schema);
            }

            return null;
        }
	}
}