/// <reference path="../../references.ts" />
/// <reference path="../util/Path.ts" />
/// <reference path="Buffer.ts" />

module appex.templates.io {

    export class IOSync {
    
        constructor() {
            
        }

        public load(filename:string) : string {
            
            if(node.fs.existsSync(filename)) {

                var content = node.fs.readFileSync(filename);

                return appex.templates.io.Buffer.process( content );            
            }

            return '[' + filename + ' not found]';
        }
    }
}