module appex.util {

    export class TypeCheck {
        
        /** test to see if this object is a boolean */
        public static isBoolean(object:any) : boolean {

            return (typeof object) === 'boolean';
        }


        /** test to see if this object is a integer */
        public static isInteger(object:any) : boolean {

            return (typeof object === "number") && Math.floor(object) === object;
        }

        /** test to see if this object is a number */
        public static isNumber(object:any) : boolean {

            return (typeof object === "number");
        }

        /** test to see if this object is an array */
        public static isArray(object:any) : boolean {

            return Object.prototype.toString.call( object ) === '[object Array]';
        }

        /** tests to see if this object is a string */
        public static isString(object:any) : boolean {

            return (typeof object) === 'string';
        }

        /** tests to see if this object is a object */
        public static isObject(object:any) : boolean {
            
            return (typeof object) === 'object' && !TypeCheck.isArray(object);
        }

        /** tests to see if this object is a function */
        public static isFunction(object:any) : boolean {
        
            return (typeof object) === 'function';
        }
    }
}