/// <reference path="../modules/IModule.ts" />
/// <reference path="routing/IRouter.ts" />
/// <reference path="IContext.ts" />

module appex.web {

    export class Context implements appex.web.IContext {
        
        /** the appex request */
        public request   : appex.web.IRequest;

        /** the appex response */
        public response  : appex.web.IResponse;

        /** the appex cascade */
        public cascade   : any;

        /** the next function */
        public next      : Function;

        /** the appex router */
        public router    : appex.web.routing.IRouter;
        
        /** the current executing module */
        public module    : appex.modules.IModule;
        
        /** mime utility */
        public mime      : appex.web.media.Mime;          
    }
}