/// <reference path="../modules/IModule.ts" />
/// <reference path="../schema/JsonSchema.ts" />
/// <reference path="routing/IRouter.ts" />
/// <reference path="sitemap/Sitemap.ts" />
/// <reference path="IContext.ts" />

module appex.web {

    export class Context implements appex.web.IContext {
        
        /** the appex request */
        public request     : appex.web.IRequest;

        /** the appex response */
        public response    : appex.web.IResponse;

        /** the appex attribute */
        public attribute   : any;

        /** the next function */
        public next        : Function;

        /** the appex router */
        public router      : appex.web.routing.IRouter;
        
        /** the appex sitemap */
        public sitemap     : appex.web.sitemap.Sitemap;

        /** the current executing module */
        public module      : appex.modules.IModule;

        /** the appex schema api */
        public schema      : appex.schema.JsonSchema;

        /** the appex template engine */
        public template    : appex.templates.Engine;
        
        /** mime utility */
        public mime        : appex.web.media.Mime;          
    }
}