/// <reference path="../references.ts" />
/// <reference path="../modules/IModule.ts" />
/// <reference path="../schema/JsonSchema.ts" />
/// <reference path="../templates/Engine.ts" />
/// <reference path="routing/IRouter.ts" />
/// <reference path="sitemap/Sitemap.ts" />
/// <reference path="IRequest.ts" />
/// <reference path="IResponse.ts" />
/// <reference path="media/Mime.ts" />


module appex.web {
    
    export interface IContext {

        /** the appex web request */
        request   : appex.web.IRequest;

        /** the appex web response */
        response  : appex.web.IResponse;

        /** the appex attribute */
        attribute   : any;

        /** the next function */
        next        : Function;

        /** the appex router */
        router    : appex.web.routing.IRouter;
        
        /** the current executing module */
        module    : appex.modules.IModule;

        /** the appex schema api */
        schema    : appex.schema.JsonSchema;

        /** the appex sitemap */
        sitemap   : appex.web.sitemap.Sitemap;

        /** the appex template engine */
        template  : appex.templates.Engine;

        /** mime utility */
        mime      : appex.web.media.Mime;

    }
}
