/// <reference path="../references.ts" />

module appex.web {

    /** A appex http request */
    export interface IRequest extends http.ServerRequest {

        /** appex headers */
        headers : string;
    }

    /** patches the nodejs http request with appex request api. 
    *   
    *   arguments:
    *       request  - the nodejs http request
    *       returns  - nodejs http + appex request api.
    */
    export function BindRequest(request: http.ServerRequest) : appex.web.IRequest {
        
        var _request:any = <appex.web.IRequest>request;

        return _request;
    }
}
