/// <reference path="../references.ts" />
/// <reference path="Cookies.ts" />

module appex.web {

    /** appex request body api */
    export class RequestBody {
    
        constructor(public request:http.ServerRequest) { }

        /** receives data submitted for this request
        *   as a string. 
        *   arguments:
        *       callback: the callback to receive data on
        */
        public recv(callback: (data:string) => void) : void {
            
            var buffer = []

            this.request.on('data', (data) => { buffer.push(data) })
            
            this.request.on('end',  () => { callback(buffer.join('')) })
        }

        /** receives json data submitted for this request
        *   and returns as a object.
        *   arguments:
        *       callback: the callback to receive data on
        */
        public json(callback: (obj:any) => void) : void {
            
            this.recv((data:string) => {

                try {

                    var obj = JSON.parse(data)

                    callback(obj)
                }
                catch(e) {

                    callback(null)
                }
            })
        }

        /** receives form submitted data for this request
        *   and returns as a object.
        *   arguments:
        *       callback: the callback to receive data on
        */
        public form(callback: (obj:any) => void) : void {
            
            this.recv((data:string) => {

                try {

                    var obj = node.querystring.parse(data)

                    callback(obj)
                }
                catch(e) {

                    callback(null)
                }
            })
        }
    }

    /** A appex http request */
    export interface IRequest extends http.ServerRequest {

        /** headers */
        headers : any;
        
        /** http request body */
        body    : appex.web.RequestBody;

        /** http cookie collection. */
        cookies : appex.web.CookieCollection;
    }

    /** patches the nodejs http request with appex request api. 
    *   
    *   arguments:
    *       request  - the nodejs http request
    *       returns  - nodejs http + appex request api.
    */
    export function BindRequest(request: http.ServerRequest) : appex.web.IRequest {
        
        var _request:any = <appex.web.IRequest>request;

        if(!_request.body)     _request.body    = new appex.web.RequestBody(request);

        if(!_request.cookies)  _request.cookies = new appex.web.CookieCollection(request);
        
        return _request;
    }
}
