/// <reference path="../../references.ts" />
/// <reference path="../routing/IRouter.ts" />
/// <reference path="../routing/IRoute.ts" />
/// <reference path="../routing/IModuleRoute.ts" />
/// <reference path="SitemapNode.ts" />

module appex.web.sitemap {

    /** appex sitemap. derives a hierarchy of sitemap nodes from the router */
    export class Sitemap extends SitemapNode {

        constructor(router:appex.web.routing.IRouter) {

            super();

            this.name = 'sitemap';

            this.initialize(router);
        }

        /** return a node based on the qualified function name. 
        *   arguments:
        *       qualifier - the fully qualified name of the function.
        */
        public get(qualifier : string) : SitemapNode {
            
            if(qualifier == '') {
            
                return this;
            }
            
            var stack   = qualifier.split('.')

            var current = <appex.web.sitemap.SitemapNode>this;

            for(var n in stack) {

                var found = false;

                for(var m in current.nodes) {
                    
                    if(current.nodes[m].name == stack[n]) {
                    
                        current = current.nodes[m]

                        found   = true;

                        break;
                    }
                }

                if(!found) {
                    
                    return null;
                }
            }

            return current;
        }  

        /** initializes the Sitemap */
        private initialize(router:appex.web.routing.IRouter) : void {

            //--------------------------------------------
            // fixes the scope (removes the forward "name")
            //--------------------------------------------        
            var fix_scope = (scope:string[]) : string[] => {
        
                var result =  []

                for(var n in scope) {
                
                    if(scope[n].indexOf('\"') == -1) {
                    
                        result.push(scope[n]);
                    }  
                }

                return result;
            }

            //--------------------------------------------
            // applies the attribute to the node.
            //--------------------------------------------            
            var apply_attribute = (node: appex.web.sitemap.SitemapNode, attribute:any) : void => {
                
                for(var n in attribute) {
                    
                    if(n != 'use') {
                    
                        node[n] = attribute[n]
                    }
                }                   
            }          

            //--------------------------------------------
            // for each route in the router.
            //--------------------------------------------
            for(var n in router.routes) {

                var route = <appex.web.routing.IModuleRoute> router.routes[n];
                
                switch(route.type) {
                
                    case 'index':
                    
                    case 'named':
                       
                    case 'wildcard':

                        var type    = route.moduleExport.type;

                        var scope   = fix_scope(type.scope);

                        var name    = type.name;

                        var current = <appex.web.sitemap.SitemapNode>this;

                        //--------------------------------------------
                        // algo to locate/create the appropriate node.
                        //--------------------------------------------

                        if(!current.nodes) current.nodes = []
                                    
                        for(var n in scope) {
                            
                            if(current.nodes) {

                                var found = false;

                                for(var m in current.nodes) {
                            
                                    if(current.nodes[m].name == scope[n]) {
                                        
                                        found = true;

                                        current = current.nodes[m];

                                        break;
                                    }
                                }
                                
                                if(!found) {
                                    
                                    var node = new appex.web.sitemap.SitemapNode();

                                    node.name = scope[n]

                                    node.nodes = []

                                    current.nodes.push(node)

                                    current = node
                                }
                            }
                        }

                        //--------------------------------------------
                        // apply this node.
                        //--------------------------------------------
                        var node     = new appex.web.sitemap.SitemapNode();
                        
                        node.name    = type.name;
                        
                        node.urls    = route.urls();
                        
                        apply_attribute(node, route.moduleExport.attribute())

                        current.nodes.push(node)

                        break;

                    default:

                        // todo: handle additional route types

                        break;
                }
            }
        }
    }
}